// client/src/components/QuizGame/QuizGame.jsx
import React, { useEffect, useState } from "react";
import "./QuizGame.css";
import Texts from "./Text";

// Import sprites explicitly so bundlers resolve them correctly
import civ1 from "../../assets/civ1.png";
import civ2 from "../../assets/civ2.png";
import civ3 from "../../assets/civ3.png";

const CiviliansSprites = [civ1, civ2, civ3];

// Normalize "correct answer" field from server (0-based, 1-based, string index, or answer text)
function getCorrectIndex(q) {
    const { answers, correctAnswerIndex } = q;

    if (typeof correctAnswerIndex === "number") {
        // If 1-based (1..answers.length), convert to 0-based
        if (correctAnswerIndex >= 1 && correctAnswerIndex <= answers.length) {
            return correctAnswerIndex - 1;
        }
        // Otherwise assume already 0-based
        return correctAnswerIndex;
    }

    if (typeof correctAnswerIndex === "string") {
        const trimmed = correctAnswerIndex.trim();

        // Case 1: numeric string
        const num = parseInt(trimmed, 10);
        if (!Number.isNaN(num)) {
            if (num >= 1 && num <= answers.length) return num - 1; // 1-based
            if (num >= 0 && num < answers.length) return num;      // 0-based
        }

        // Case 2: the field is the answer text itself
        const idx = answers.findIndex((a) => String(a).trim() === trimmed);
        if (idx !== -1) return idx;
    }

    // Fallback to first answer
    return 0;
}

const QuizGame = ({ quizId, onFinish }) => {
    const [questions, setQuestions] = useState([]);
    const [currentIndex, setCurrentIndex] = useState(0);
    const [feedback, setFeedback] = useState("");
    const [citizenCount, setCitizenCount] = useState(1);
    const [score, setScore] = useState(0);
    const [isLocked, setIsLocked] = useState(false);
    const [isLoading, setIsLoading] = useState(false);
    const [loadError, setLoadError] = useState("");
    const [finished, setFinished] = useState(false);

    // Reset when quizId changes
    useEffect(() => {
        setQuestions([]);
        setCurrentIndex(0);
        setFeedback("");
        setCitizenCount(1);
        setScore(0);
        setIsLocked(false);
        setIsLoading(false);
        setLoadError("");
        setFinished(false);
    }, [quizId]);

    // Fetch questions from API
    useEffect(() => {
        const fetchQuestions = async () => {
            if (!quizId) return;
            setIsLoading(true);
            setLoadError("");

            try {
                const res = await fetch(`/api/questions/${quizId}`);
                if (!res.ok) throw new Error(`HTTP ${res.status}`);
                const data = await res.json();

                if (!Array.isArray(data) || data.length === 0) {
                    throw new Error("Empty questions array");
                }

                setQuestions(data);
            } catch (err) {
                console.error("Failed to fetch questions:", err);
                setLoadError("Failed to load questions. Please try again.");
            } finally {
                setIsLoading(false);
            }
        };

        fetchQuestions();
    }, [quizId]);

    if (!quizId) return <div>{Texts.no_quiz_selected}</div>;
    if (isLoading) return <div>{Texts.loading_questions}</div>;
    if (loadError) return <div>{loadError}</div>;
    if (questions.length === 0) return <div>{Texts.loading_questions}</div>;

    const currentQuestion = questions[currentIndex];

    const handleAnswerClick = (index) => {
        if (isLocked || finished) return;
        setIsLocked(true);

        const correctIdx = getCorrectIndex(currentQuestion);
        const isCorrect = index === correctIdx;

        if (isCorrect) {
            setFeedback(Texts.correct_answer_alert);
            setScore((s) => s + 1);
            setCitizenCount((prev) => Math.min(prev + 1, CiviliansSprites.length));
        } else {
            setFeedback(Texts.wrong_answer_alert);
            setCitizenCount((prev) => Math.max(prev - 1, 1));
        }

        setTimeout(() => {
            setFeedback("");
            setIsLocked(false);

            setCurrentIndex((prev) => {
                const next = prev + 1;
                if (next < questions.length) {
                    return next;
                } else {
                    // Finish quiz
                    const finalScore = isCorrect ? score + 1 : score;
                    setFinished(true);
                    if (typeof onFinish === "function") {
                        onFinish({ score: finalScore, total: questions.length });
                    }
                    return prev; // stay on last index; UI switches to end-screen
                }
            });
        }, 1000);
    };

    const handleRestart = () => {
        setCurrentIndex(0);
        setFeedback("");
        setCitizenCount(1);
        setScore(0);
        setIsLocked(false);
        setFinished(false);
    };

    return (
        <div className="quiz-container">
            {/* Left side */}
            <div className="left-side">
                <div className="info-panel">
                    <div className="citizen-count-box">
                        {Texts.citizen_counter_label} {citizenCount}
                    </div>

                    <div className={`feedback-box ${feedback ? "visible" : ""}`}>
                        {feedback}
                    </div>

                    <div className="game-frame">
                        <div className="citizens-panel">
                            {CiviliansSprites.slice(0, citizenCount).map((src, idx) => (
                                <img
                                    key={idx}
                                    src={src}
                                    alt={`Citizen ${idx + 1}`}
                                    className="citizen-img"
                                />
                            ))}
                        </div>
                    </div>
                </div>
            </div>

            {/* Right side */}
            <div className="right-side">
                {finished ? (
                    <div className="end-screen">
                        <h3>{Texts.end_title}</h3>
                        <p>{Texts.end_summary(score, questions.length)}</p>
                        <button className="restart-button" onClick={handleRestart}>
                            {Texts.restart}
                        </button>
                    </div>
                ) : (
                    <>
                        <div className="question-box">{currentQuestion.question}</div>

                        <div className="answers-grid">
                            {currentQuestion.answers.map((ans, idx) => (
                                <button
                                    key={idx}
                                    className="answer-button"
                                    onClick={() => handleAnswerClick(idx)}
                                    disabled={isLocked}
                                >
                                    {ans}
                                </button>
                            ))}
                        </div>
                    </>
                )}
            </div>
        </div>
    );
};

export default QuizGame;
