import React from "react";
import { APIProvider, Map } from "@vis.gl/react-google-maps";
import Header from "../../components/Header/Header";
import Footer from "../../components/Footer/Footer";
import SchoolList from "../../components/SchoolsList/SchoolList";
import "./LeaderboardOverlay.css";

const LeaderboardPage = () => {
  const apiKey = process.env.REACT_APP_GOOGLE_MAPS_API_KEY;

  return (
    <>
      <Header />

      {/* עטיפה משותפת: relative; המפה וה-overlay אחים */}
      <div className="lb-map-wrap">
        {/* ה-APIProvider עוטף רק את המפה */}
        <APIProvider apiKey={apiKey} onLoad={() => console.log("Maps API loaded")}>
          <div className="lb-map">
            <Map
              defaultCenter={{ lat: 32.0853, lng: 34.7818 }} // Tel Aviv
              defaultZoom={8}
              gestureHandling="greedy"
              disableDefaultUI={false}
            />
          </div>
        </APIProvider>

        {/* ה-overlay – מחוץ ל-APIProvider, אבל בתוך אותו wrapper */}
        <div className="lb-overlay-left">
          <SchoolList />
        </div>
      </div>

      <Footer />
    </>
  );
};

export default LeaderboardPage;
