import React from "react";
import { GoogleMap, Marker, LoadScript } from "@react-google-maps/api";

const DEFAULT_CENTER = { lat: 31.5, lng: 34.75 }; // Israel

export default function SchoolMap({ center = DEFAULT_CENTER, zoom = 7 }) {
    const apiKey = process.env.REACT_APP_GOOGLE_MAPS_API_KEY || "";

    // Log בטוח לזיהוי שהמפתח נטען (6 התווים האחרונים בלבד)
    if (!apiKey) {
        console.error("Missing Google Maps API key. Put it in client/.env and restart npm start");
        return <div className="lb-placeholder">Missing Google Maps API key</div>;
    } else {
        console.debug("Maps key loaded (masked): ****" + apiKey.slice(-6));
    }

    return (
        <LoadScript googleMapsApiKey={apiKey}>
            <GoogleMap
                mapContainerStyle={{ width: "100%", height: "70vh" }} // חשוב: גובה קשיח
                center={center}
                zoom={zoom}
                options={{
                    // תתחיל עם UI דיפולטי כדי לראות לוגו/קונטרולים, אחר כך נכבה
                    disableDefaultUI: false,
                    zoomControl: true,
                }}
                onLoad={() => console.debug("Google Maps loaded via LoadScript")}
            >
                {/* דמו: סמן בתל אביב */}
                <Marker position={{ lat: 32.0853, lng: 34.7818 }} />
            </GoogleMap>
        </LoadScript>
    );
}
