import React, { useEffect, useState } from "react";
import "./SchoolLis.css";

const SchoolList = () => {
    const [schools, setSchools] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        fetch("/api/schools")
            .then((res) => res.json())
            .then((data) => {
                setSchools(data);
                setLoading(false);
            })
            .catch((err) => {
                console.error("Error fetching schools:", err);
                setLoading(false);
            });
    }, []);

    if (loading) return <div className="school-list">טוען נתונים...</div>;

    return (
        <div className="school-list">
            <h2>בתי ספר מובילים</h2>
            <ul>
                {schools.map((school) => (
                    <li key={school.id} className="school-item">
                        <div className="school-name">{school.name}</div>
                        <div className="school-points">{school.points} נק'</div>
                        <div className="school-city">{school.city}</div>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default SchoolList;
