import React, { useEffect, useMemo, useState } from "react";
import { useParams } from "react-router-dom";
import "./QuizGame.css";

// Texts inline (שומרים את הקוד באנגלית)
const Texts = {
    loading: "Loading questions...",
    error: "Failed to load questions.",
    correct: "Correct! Great job!",
    wrong: "Oops! That's not correct.",
    citizens: "Citizens:",
    score: "Score:",
    endTitle: "Quiz finished!",
    endSummary: (score, total) => `You scored ${score} out of ${total}.`,
    restart: "Restart",
};

// Sprites (ודא/י שהקבצים קיימים בתיקייה הזו)
import civ1 from "../../assets/civ1.png";
import civ2 from "../../assets/civ2.png";
import civ3 from "../../assets/civ3.png";
const CIVS = [civ1, civ2, civ3];
const MAX_CITIZENS = CIVS.length;

const QuizGame = ({ onFinish }) => {
    const { id } = useParams();
    const quizId = Number(id) > 0 ? Number(id) : 1;

    // Game state
    const [questions, setQuestions] = useState([]);
    const [idx, setIdx] = useState(0);
    const [feedback, setFeedback] = useState("");
    const [citizens, setCitizens] = useState(0); // ⬅️ מתחיל מ-0
    const [score, setScore] = useState(0);       // ⬅️ מתחיל מ-0
    const [locked, setLocked] = useState(false);
    const [loading, setLoading] = useState(false);
    const [loadError, setLoadError] = useState("");
    const [finished, setFinished] = useState(false);

    // Load questions from server
    useEffect(() => {
        let cancel = false;

        const load = async () => {
            setLoading(true);
            setLoadError("");
            setQuestions([]);
            setIdx(0);
            setFeedback("");
            setCitizens(0); // reset to 0 on new quiz
            setScore(0);
            setLocked(false);
            setFinished(false);

            try {
                const res = await fetch(`/api/quiz/${quizId}`);
                if (!res.ok) throw new Error(`HTTP ${res.status}`);
                const data = await res.json();

                // Expect: { quizId, count, questions: [{ question, answers, correctAnswer }] }
                const list = Array.isArray(data?.questions) ? data.questions : [];
                const normalized = list.map((q) => ({
                    question: q.question ?? "",
                    answers: (q.answers ?? [])
                        .map((a) => String(a ?? "").trim())
                        .filter(Boolean), // remove empty strings
                    correctAnswer: String(q.correctAnswer ?? "").trim(),
                }));

                if (!cancel) setQuestions(normalized);
            } catch (e) {
                console.error(e);
                if (!cancel) setLoadError(Texts.error);
            } finally {
                if (!cancel) setLoading(false);
            }
        };

        load();
        return () => { cancel = true; };
    }, [quizId]);

    const cur = useMemo(() => (questions.length ? questions[idx] : null), [questions, idx]);

    if (loading) return <div className="status">{Texts.loading}</div>;
    if (loadError) return <div className="status error">{loadError}</div>;
    if (!cur) return <div className="status">{Texts.loading}</div>;

    const handleClick = (answerText) => {
        if (locked || finished) return;
        setLocked(true);

        const clicked = String(answerText ?? "").trim();
        const correct = String(cur.correctAnswer ?? "").trim();
        const isCorrect = clicked === correct;

        if (isCorrect) {
            setFeedback(Texts.correct);
            setScore((s) => s + 1);
            setCitizens((c) => Math.min(c + 1, MAX_CITIZENS));
        } else {
            setFeedback(Texts.wrong);
            setCitizens((c) => Math.max(c - 1, 0)); // ⬅️ לא יורד מתחת ל-0
        }

        setTimeout(() => {
            setFeedback("");
            setLocked(false);

            setIdx((prev) => {
                const next = prev + 1;
                if (next < questions.length) return next;

                // finish
                const finalScore = isCorrect ? score + 1 : score;
                setFinished(true);
                if (typeof onFinish === "function") {
                    onFinish({ score: finalScore, total: questions.length });
                }
                return prev;
            });
        }, 800);
    };

    const restart = () => {
        setIdx(0);
        setFeedback("");
        setCitizens(0);
        setScore(0);
        setLocked(false);
        setFinished(false);
    };

    return (
        <div className="quiz-container">
            {/* LEFT SIDE: status + citizens */}
            <div className="left-side">
                <div className="info-panel">
                    <div className="pill">{Texts.citizens} {citizens}</div>
                    <div className="pill">{Texts.score} {score}</div>

                    <div className={`feedback ${feedback ? "visible" : ""}`}>
                        {feedback}
                    </div>

                    <div className="game-frame">
                        <div className="citizens-panel">
                            {CIVS.slice(0, citizens).map((src, i) => (
                                <img key={i} src={src} alt={`Citizen ${i + 1}`} className="citizen-img" />
                            ))}
                        </div>
                    </div>
                </div>
            </div>

            {/* RIGHT SIDE: question/answers or end screen */}
            <div className="right-side">
                {finished ? (
                    <div className="end">
                        <h3>{Texts.endTitle}</h3>
                        <p>{Texts.endSummary(score, questions.length)}</p>
                        <button className="btn" onClick={restart}>{Texts.restart}</button>
                    </div>
                ) : (
                    <>
                        <div className="question">{cur.question}</div>
                        <div className="answers">
                            {cur.answers.map((ans, i) => (
                                <button
                                    key={i}
                                    className="btn answer"
                                    onClick={() => handleClick(ans)}
                                    disabled={locked}
                                >
                                    {ans}
                                </button>
                            ))}
                        </div>
                    </>
                )}
            </div>
        </div>
    );
};

export default QuizGame;
