import React from "react";
import { GoogleMap, Marker, useJsApiLoader } from "@react-google-maps/api";

// Default center: Israel
const DEFAULT_CENTER = { lat: 31.5, lng: 34.75 };

export default function SchoolMap({ center = DEFAULT_CENTER, zoom = 7 }) {
    const apiKey = process.env.REACT_APP_GOOGLE_MAPS_API_KEY || "";

    // Safety: show clear message if key missing
    if (!apiKey) {
        console.error("Missing Google Maps API key. Put it in client/.env and restart npm start");
        return <div className="lb-placeholder">Missing Google Maps API key</div>;
    }

    const { isLoaded, loadError } = useJsApiLoader({
        id: "qq-map",
        googleMapsApiKey: apiKey,
    });

    if (loadError) {
        console.error("Google Maps load error:", loadError);
        return <div className="lb-placeholder">Failed to load map</div>;
    }
    if (!isLoaded) return <div className="lb-placeholder">Loading mapâ€¦</div>;

    return (
        <GoogleMap
            mapContainerStyle={{ width: "100%", height: "70vh" }} // important: fixed height
            center={center}
            zoom={zoom}
            options={{ disableDefaultUI: true, zoomControl: true }}
            onLoad={() => console.debug("Google Maps loaded")}
        >
            {/* Demo marker: Tel Aviv */}
            <Marker position={{ lat: 32.0853, lng: 34.7818 }} />
        </GoogleMap>
    );
}
