import React, { useState } from "react";
import "./QuizGame.css";

const QuizGame = () => {
    const [score, setScore] = useState(0); // ניקוד השחקן

    const CiviliansSprites = [
        require("../../assets/civ1.png"),
        require("../../assets/civ2.png"),
        require("../../assets/civ3.png"),
    ];

    const handleCorrectAnswer = () => {
        if (score < CiviliansSprites.length - 1) {
            setScore(score + 1); // מוסיף נקודה (וגם אזרח נוסף)
        }
    };

    return (
        <div className="game-frame">
            <div className="quiz-container">
                <div className="left-side">
                    <div className="info-panel">
                        <div className="citizen-count-box">מספר האזרחים: {score + 1}</div>
                        <div className="feedback-box">נכון! כל הכבוד!</div>
                    </div>
                </div>

                <div className="right-side">
                    <div className="question-box">כמה זה 2 + 2?</div>
                    <div className="answers-grid">
                        <button onClick={() => handleCorrectAnswer()}>1</button>
                        <button onClick={() => handleCorrectAnswer()}>4</button> {/* נכונה */}
                        <button onClick={() => handleCorrectAnswer()}>3</button>
                        <button onClick={() => handleCorrectAnswer()}>2</button>
                    </div>
                </div>
            </div>

            {/* הצגת האזרחים לפי ניקוד */}
            <div className="citizens-panel">
                {CiviliansSprites.slice(0, score + 1).map((src, index) => (
                    <img
                        key={index}
                        src={src}
                        alt={`אזרח ${index + 1}`}
                        className="citizen-img"
                    />
                ))}
            </div>
        </div>
    );
};

export default QuizGame;
