import React, { useEffect, useMemo, useState } from "react";
import "./QuizGame.css";
import Texts from "./Text";

const QuizGame = ({ quizId, onFinish }) => {
    const [questions, setQuestions] = useState([]);
    const [currentIndex, setCurrentIndex] = useState(0);
    const [feedback, setFeedback] = useState("");
    const [citizenCount, setCitizenCount] = useState(1);
    const [score, setScore] = useState(0);
    const [isLocked, setIsLocked] = useState(false); // prevent double clicks
    const [isLoading, setIsLoading] = useState(false);
    const [loadError, setLoadError] = useState("");

    // Keep as require if את מריצה עם CRA/Webpack
    const CiviliansSprites = useMemo(
        () => [
            require("../../assets/civ1.png"),
            require("../../assets/civ2.png"),
            require("../../assets/civ3.png"),
        ],
        []
    );

    // Reset state on quiz change
    useEffect(() => {
        setQuestions([]);
        setCurrentIndex(0);
        setFeedback("");
        setCitizenCount(1);
        setScore(0);
        setIsLocked(false);
        setLoadError("");
    }, [quizId]);

    // Fetch questions
    useEffect(() => {
        const fetchQuestions = async () => {
            if (!quizId) return;
            setIsLoading(true);
            setLoadError("");
            try {
                const res = await fetch(`/api/questions/${quizId}`);
                if (!res.ok) throw new Error(`HTTP ${res.status}`);
                const data = await res.json();

                // Expecting: [{ question: string, answers: [a,b,c,d], correctAnswerIndex: number }, ...]
                if (!Array.isArray(data) || data.length === 0) {
                    throw new Error("Empty questions array");
                }
                setQuestions(data);
            } catch (err) {
                console.error("Failed to fetch questions:", err);
                setLoadError("Failed to load questions. Please try again.");
            } finally {
                setIsLoading(false);
            }
        };
        fetchQuestions();
    }, [quizId]);

    if (!quizId) return <div>{Texts.no_quiz_selected}</div>;
    if (isLoading) return <div>{Texts.loading_questions}</div>;
    if (loadError) return <div>{loadError}</div>;
    if (questions.length === 0) return <div>{Texts.loading_questions}</div>;

    const currentQuestion = questions[currentIndex];

    const handleAnswerClick = (index) => {
        if (isLocked) return;
        setIsLocked(true);

        const isCorrect = index === currentQuestion.correctAnswerIndex;

        if (isCorrect) {
            setFeedback(Texts.correct_answer_alert);
            setScore((s) => s + 1);
            setCitizenCount((prev) => Math.min(prev + 1, CiviliansSprites.length));
        } else {
            setFeedback(Texts.wrong_answer_alert);
            setCitizenCount((prev) => Math.max(prev - 1, 1));
        }

        setTimeout(() => {
            setFeedback("");
            setIsLocked(false);

            setCurrentIndex((prev) => {
                const next = prev + 1;
                if (next < questions.length) return next;

                // Finished
                if (typeof onFinish === "function") {
                    onFinish({ score: score + (isCorrect ? 1 : 0), total: questions.length });
                }
                return prev; // stop advancing; we'll render end screen
            });
        }, 1000);
    };

    const isFinished = currentIndex === questions.length - 1 && feedback === "" && isLocked === false && score + 0 === score && false;
    // הערה: נבחר להציג מסך סיום בצורה מפורשת להלן במקום תנאי מסובך

    const showEndScreen = currentIndex === questions.length - 1 && feedback === "" && !isLoading && !isLocked
        ? false // נשאיר false כדי להמשיך לשאלה האחרונה; נציג סיום רק ע"י כפתור Restart עם תנאי אחר בהמשך
        : false;

    const handleRestart = () => {
        setCurrentIndex(0);
        setFeedback("");
        setCitizenCount(1);
        setScore(0);
        setIsLocked(false);
    };

    const isLastQuestion = currentIndex === questions.length - 1;

    return (
        <div className="quiz-container">
            {/* Left side */}
            <div className="left-side">
                <div className="info-panel">
                    <div className="citizen-count-box">
                        {Texts.citizen_counter_label} {citizenCount}
                    </div>
                    <div className={`feedback-box ${feedback ? "visible" : ""}`}>{feedback}</div>

                    <div className="game-frame">
                        <div className="citizens-panel">
                            {CiviliansSprites.slice(0, citizenCount).map((src, idx) => (
                                <img key={idx} src={src} alt={`Citizen ${idx + 1}`} className="citizen-img" />
                            ))}
                        </div>
                    </div>
                </div>
            </div>

            {/* Right side */}
            <div className="right-side">
                {/* End screen */}
                {isLastQuestion && !feedback && (
                    <div className="end-screen">
                        <h3>{Texts.end_title}</h3>
                        <p>{Texts.end_summary(score, questions.length)}</p>
                        <button className="restart-button" onClick={handleRestart}>
                            {Texts.restart}
                        </button>
                    </div>
                )}

                {/* Question/answers */}
                {!(!feedback && isLastQuestion) && (
                    <>
                        <div className="question-box">{currentQuestion.question}</div>
                        <div className="answers-grid">
                            {currentQuestion.answers.map((ans, idx) => (
                                <button
                                    key={idx}
                                    className="answer-button"
                                    onClick={() => handleAnswerClick(idx)}
                                    disabled={isLocked}
                                >
                                    {ans}
                                </button>
                            ))}
                        </div>
                    </>
                )}
            </div>
        </div>
    );
};

export default QuizGame;
