import React from "react";
import Header from "../../components/Header/Header";
import Footer from "../../components/Footer/Footer";
import "./LeaderboardPage.css";

export default function LeaderboardPage() {
  // Page-level state (reserved for later use)
  const [selectedId, setSelectedId] = React.useState(null);
  const [query, setQuery] = React.useState("");
  const [minPlayers, setMinPlayers] = React.useState(0);

  return (
    <>
      <Header />
      <main className="lb-layout">
        {/* Map section (left) */}
        <section className="lb-map">
          <div className="lb-card">
            <h2 className="lb-title">Map</h2>
            <div className="lb-map-body">
              {/* TODO: replace with <SchoolMap /> */}
              <div className="lb-placeholder">Map goes here</div>
            </div>
          </div>
        </section>

        {/* List section (right) */}
        <aside className="lb-list">
          <div className="lb-card">
            <h2 className="lb-title">Leaderboard</h2>

            {/* Basic controls placeholders */}
            <div className="lb-controls">
              <input
                type="text"
                placeholder="Search schoolâ€¦"
                value={query}
                onChange={(e) => setQuery(e.target.value)}
              />
              <input
                type="number"
                min={0}
                placeholder="Min players"
                value={minPlayers}
                onChange={(e) => setMinPlayers(Number(e.target.value) || 0)}
              />
            </div>

            <div className="lb-list-body">
              {/* TODO: replace with <SchoolList /> */}
              <div className="lb-placeholder">List goes here</div>
            </div>
          </div>
        </aside>
      </main>
      <Footer />
    </>
  );
}
