import React from "react";
import { APIProvider, Map } from "@vis.gl/react-google-maps";
import Header from "../../components/Header/Header";
import Footer from "../../components/Footer/Footer";
import SchoolList from "../../components/SchoolsList/SchoolList";

const LeaderboardPage = () => {
  const apiKey = process.env.REACT_APP_GOOGLE_MAPS_API_KEY;

  return (
    <>
      <Header />

      {/* 2-column layout: left = schools list (fixed), right = map (flex) */}
      <main
        style={{
          display: "grid",
          gridTemplateColumns: "360px 1fr",
          gap: 16,
          padding: 16,
          boxSizing: "border-box",
          alignItems: "start",
        }}
      >
        {/* Left column */}
        <SchoolList />

        {/* Right column (map) */}
        <APIProvider apiKey={apiKey} onLoad={() => console.log("Maps API loaded")}>
          <div
            style={{
              height: 735,
              borderRadius: 12,
              overflow: "hidden",
              background: "#eef1f4",
              minWidth: 0, // important in grids/flex to allow the map to size correctly
            }}
          >
            <Map
              defaultCenter={{ lat: 32.0853, lng: 34.7818 }} // Tel Aviv
              defaultZoom={8}
              gestureHandling="greedy"
              disableDefaultUI={false}
            />
          </div>
        </APIProvider>
      </main>

      <Footer />
    </>
  );
};

export default LeaderboardPage;
