import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
//import "./Header.css"; // אם תרצה להפריד גם CSS

const Header = ({ userData, setUserData }) => {
  const navigate = useNavigate();
  const [menuOpen, setMenuOpen] = useState(false);

  const toggleMenu = () => setMenuOpen((prev) => !prev);

  const handleLoginNavigate = () => {
    navigate("/login");
    setMenuOpen(false);
  };

  const handleLogout = () => {
    localStorage.removeItem("user");
    setUserData(null);
    setMenuOpen(false);
  };

  const handleRegister = () => {
    navigate("/register");
  };

  return (
    <header>
      <button>⚙️ Settings</button>
      <div className="logo-container">
        <img src="/assets/QQ.png" alt="QuizQuest Logo" className="logo" />
      </div>
      <div className="profile-container">
        <img
          src={userData ? "/assets/user.png" : "/assets/guest.png"}
          alt="Profile"
          className="profile-button"
          onClick={toggleMenu}
        />
        {menuOpen && (
          <div className="profile-menu">
            {!userData && <button onClick={handleLoginNavigate}>Log In</button>}
            {userData && <button onClick={handleLogout}>Sign Out</button>}
            <button onClick={handleRegister}>Register</button>
          </div>
        )}
      </div>
    </header>
  );
};

export default Header;
