import React, { useState } from "react";
import "./HomePage.css";
import logo from "../assets/QQ.png";
import guestImage from "../assets/guest.png"; // תמונת אורח
import userImage from "../assets/user.png"; // תמונת פרופיל מדומה
import { useNavigate } from "react-router-dom"; // כדי להשתמש בניווט

const HomePage = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false); // ניהול מצב התחברות
  const [menuOpen, setMenuOpen] = useState(false); // ניהול תפריט
  const navigate = useNavigate();

  const toggleMenu = () => {
    setMenuOpen((prev) => !prev);
  };

  const handleLoginNavigate = () => {
    navigate("/login"); // ניווט לדף ההתחברות
    setMenuOpen(false); // סגור את התפריט
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    setMenuOpen(false); // סגור את התפריט אחרי היציאה
  };

  const handleRegister = () => {
    navigate("/register"); // מעבר לנתיב ההרשמה
  };

  return (
    <div>
      {/* כ עליונה */}
      <header>
        <button>⚙️ Settings</button>
        <div className="logo-container">
          <img src={logo} alt="QuizQuest Logo" className="logo" />
        </div>
        <div className="profile-container">
          <img
            src={isLoggedIn ? userImage : guestImage}
            alt="Profile"
            className="profile-button"
            onClick={toggleMenu}
          />
          {menuOpen && (
            <div className="profile-menu">
              {!isLoggedIn && <button onClick={handleLoginNavigate}>Log In</button>}
              {isLoggedIn && <button onClick={handleLogout}>Sign Out</button>}
              <button onClick={handleRegister}>Register</button>
            </div>
          )}
        </div>
      </header>

      {/* תפריט מרכזי */}
      <main>
        <button>Start Quiz</button>
        <button>Create Quiz</button>
        <button>Leaderboard</button>
      </main>

      {/* כותרת תחתונה */}
      <footer>
        <p>© QuizQuest</p>
      </footer>
    </div>
  );
};

export default HomePage;
