import React, { useEffect, useState, useMemo } from "react";
import "./SchoolList.css";

/**
 * Props:
 * - onSelect?: (school) => void   // optional; called when a school is clicked
 * - initialSelectedId?: number    // optional; preselect a school by id
 */
const SchoolList = ({ onSelect, initialSelectedId = null }) => {
    const [schools, setSchools] = useState([]);
    const [loading, setLoading] = useState(true);
    const [err, setErr] = useState("");
    const [selectedId, setSelectedId] = useState(initialSelectedId);
    const [q, setQ] = useState("");

    useEffect(() => {
        let isMounted = true;
        setLoading(true);
        fetch("/api/schools")
            .then((r) => {
                if (!r.ok) throw new Error("Failed to load schools");
                return r.json();
            })
            .then((data) => {
                if (!isMounted) return;
                setSchools(Array.isArray(data) ? data : []);
                setLoading(false);
            })
            .catch((e) => {
                if (!isMounted) return;
                console.error("Error fetching schools:", e);
                setErr("Could not load schools.");
                setLoading(false);
            });
        return () => {
            isMounted = false;
        };
    }, []);

    const filtered = useMemo(() => {
        const term = q.trim().toLowerCase();
        if (!term) return schools;
        return schools.filter((s) =>
            [s.name, s.city].filter(Boolean).some((v) => v.toLowerCase().includes(term))
        );
    }, [q, schools]);

    const handleClick = (s) => {
        setSelectedId(s.id);
        onSelect && onSelect(s); // optional integration hook
    };

    return (
        <aside className="school-list" aria-label="Top schools">
            <div className="school-list__header">
                <h2>Top Schools</h2>
                <input
                    className="school-list__search"
                    type="text"
                    placeholder="Search…"
                    value={q}
                    onChange={(e) => setQ(e.target.value)}
                    aria-label="Search schools"
                />
            </div>

            {loading && <div className="school-list__status">Loading…</div>}
            {err && !loading && <div className="school-list__error">{err}</div>}

            {!loading && !err && (
                <ul className="school-list__ul">
                    {filtered.map((s, idx) => {
                        const isActive = s.id === selectedId;
                        return (
                            <li
                                key={s.id}
                                className={`school-list__item ${isActive ? "is-active" : ""}`}
                                onClick={() => handleClick(s)}
                                role="button"
                                tabIndex={0}
                                onKeyDown={(e) => (e.key === "Enter" || e.key === " ") && handleClick(s)}
                                title={s.name}
                            >
                                <div className="school-list__rank">{idx + 1}</div>
                                <div className="school-list__main">
                                    <div className="school-list__name">{s.name}</div>
                                    <div className="school-list__meta">{s.city || "—"}</div>
                                </div>
                                <div className="school-list__points">{s.points ?? 0} pts</div>
                            </li>
                        );
                    })}
                    {filtered.length === 0 && (
                        <li className="school-list__empty">No schools found.</li>
                    )}
                </ul>
            )}
        </aside>
    );
};

export default SchoolList;
