import React from "react";
import { GoogleMap, Marker, LoadScript } from "@react-google-maps/api";

const DEFAULT_CENTER = { lat: 31.5, lng: 34.75 };

export default function SchoolMap({ center = DEFAULT_CENTER, zoom = 7 }) {
    const apiKey = process.env.REACT_APP_GOOGLE_MAPS_API_KEY || "";
    if (!apiKey) return <div className="lb-placeholder">Missing Google Maps API key</div>;

    return (
        <LoadScript googleMapsApiKey={apiKey}>
            <GoogleMap
                mapContainerClassName="qq-map"   // ← במקום mapContainerStyle
                center={center}
                zoom={zoom}
                options={{ disableDefaultUI: false, zoomControl: true }}
            >
                <Marker position={{ lat: 32.0853, lng: 34.7818 }} />
            </GoogleMap>
        </LoadScript>
    );
}
