import React, { useState, useEffect } from "react";

const QuizGame = () => {
    const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
    const [citizens, setCitizens] = useState(1);
    const [questions, setQuestions] = useState([]);
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {
        // אפשר להחליף בקריאה לשרת בהמשך
        const dummyQuestions = [
            {
                question: "What is the capital of France?",
                options: ["Berlin", "Madrid", "Paris", "Rome"],
                correct: "Paris",
            },
            {
                question: "2 + 2 = ?",
                options: ["3", "4", "5", "6"],
                correct: "4",
            },
        ];
        setQuestions(dummyQuestions);
        setIsLoading(false);
    }, []);

    const handleAnswer = (selected) => {
        const current = questions[currentQuestionIndex];
        const isCorrect = selected === current.correct;

        if (isCorrect) {
            setCitizens((prev) => prev + 1);
        } else {
            setCitizens((prev) => (prev > 1 ? prev - 1 : 1));
        }

        // אם יש עוד שאלות - עבור לשאלה הבאה
        if (currentQuestionIndex < questions.length - 1) {
            setCurrentQuestionIndex((prev) => prev + 1);
        } else {
            alert("Game Over! Citizens: " + citizens);
        }
    };

    if (isLoading) return <p>Loading...</p>;

    const current = questions[currentQuestionIndex];

    return (
        <div className="quiz-game">
            <h2>Citizens: {citizens}</h2>
            <h3>{current.question}</h3>
            {current.options.map((opt, i) => (
                <button key={i} onClick={() => handleAnswer(opt)}>
                    {opt}
                </button>
            ))}
        </div>
    );
};

export default QuizGame;
