import React from "react";
import Header from "../../components/Header/Header";
import Footer from "../../components/Footer/Footer";

// Google Maps (using @react-google-maps/api)
import { LoadScript, GoogleMap, Marker } from "@react-google-maps/api";

const DEFAULT_CENTER = { lat: 31.5, lng: 34.75 }; // Israel

// Inline styles (no external CSS)
const styles = {
  layout: {
    display: "grid",
    gridTemplateColumns: "minmax(0, 1fr) 380px",
    gap: 24,
    padding: 24,
    alignItems: "start",
  },
  card: {
    background: "#fff",
    borderRadius: 18,
    boxShadow: "0 8px 20px rgba(0,0,0,.06)",
    padding: 16,
    display: "flex",
    flexDirection: "column",
  },
  title: { margin: "0 0 12px", fontSize: 20, fontWeight: 700, textAlign: "center" },
  // mapBody: {
  //   // critical: explicit height so Google Map actually renders
  //   height: "70vh",
  //   position: "relative",
  // },
  listBody: {
    maxHeight: "70vh",
    overflow: "auto",
  },
  input: {
    width: "100%",
    padding: "10px 12px",
    borderRadius: 10,
    border: "1px solid #e4e6eb",
    outline: "none",
  },
  placeholder: {
    border: "2px dashed #cfd4db",
    borderRadius: 12,
    display: "grid",
    placeItems: "center",
    color: "#7a8699",
    fontSize: 14,
    height: "100%",
  },
};

export default function LeaderboardPage() {
  const [query, setQuery] = React.useState("");
  const apiKey = process.env.REACT_APP_GOOGLE_MAPS_API_KEY || "";

  return (
    <>
      <Header />
      <main style={styles.layout}>
        {/* Map section (left) */}
        <section>
          <div style={styles.card}>
            <h2 style={styles.title}>Map</h2>

            <div style={styles.mapBody}>
              {!apiKey ? (
                <div style={styles.placeholder}>
                  Missing Google Maps API key
                  <br />
                  (set <code>REACT_APP_GOOGLE_MAPS_API_KEY</code> in <code>client/.env</code> and restart)
                </div>
              ) : (
                <LoadScript
                  googleMapsApiKey={apiKey}
                  onError={(e) => console.error("LoadScript error", e)}
                  onLoad={() => console.debug("Maps JS loaded")}
                >
                  {/* No external CSS: give the map its own size right here */}
                  <GoogleMap
                    mapContainerStyle={{
                      width: "100%",
                      height: "100%", // fill parent (which is 70vh)
                      borderRadius: 12,
                      overflow: "hidden",
                    }}
                    center={DEFAULT_CENTER}
                    zoom={7}
                    options={{ disableDefaultUI: false, zoomControl: true }}
                    onLoad={() => console.debug("GoogleMap created")}
                  >
                    {/* Demo marker: Tel Aviv */}
                    <Marker position={{ lat: 32.0853, lng: 34.7818 }} />
                  </GoogleMap>
                </LoadScript>
              )}
            </div>
          </div>
        </section>

        {/* List section (right) */}
        <aside>
          <div style={styles.card}>
            <h2 style={styles.title}>Leaderboard</h2>

            <div style={{ marginBottom: 12 }}>
              <input
                type="text"
                placeholder="Search schoolâ€¦"
                value={query}
                onChange={(e) => setQuery(e.target.value)}
                style={styles.input}
              />
            </div>

            <div style={styles.listBody}>
              <div style={styles.placeholder}>List goes here</div>
            </div>
          </div>
        </aside>
      </main>
      <Footer />
    </>
  );
}
