import React from "react";
import Header from "../../components/Header/Header";
import Footer from "../../components/Footer/Footer";
import "./LeaderboardPage.css";

// Google Maps (using @react-google-maps/api)
import { LoadScript, GoogleMap, Marker } from "@react-google-maps/api";

const DEFAULT_CENTER = { lat: 31.5, lng: 34.75 }; // Israel

export default function LeaderboardPage() {
  const [selectedId, setSelectedId] = React.useState(null);
  const [query, setQuery] = React.useState("");

  const apiKey = process.env.REACT_APP_GOOGLE_MAPS_API_KEY || "";

  return (
    <>
      <Header />
      <main className="lb-layout">
        {/* Map section (left) */}
        <section className="lb-map">
          <div className="lb-card">
            <h2 className="lb-title">Map</h2>

            <div className="lb-map-body">
              {!apiKey ? (
                <div className="lb-placeholder">
                  Missing Google Maps API key (set REACT_APP_GOOGLE_MAPS_API_KEY in client/.env)
                </div>
              ) : (
                <LoadScript googleMapsApiKey={apiKey}>
                  <GoogleMap
                    mapContainerClassName="qq-map"
                    center={DEFAULT_CENTER}
                    zoom={7}
                    options={{ disableDefaultUI: false, zoomControl: true }}
                  >
                    {/* Demo marker: Tel Aviv */}
                    <Marker position={{ lat: 32.0853, lng: 34.7818 }} />
                  </GoogleMap>
                </LoadScript>
              )}
            </div>
          </div>
        </section>

        {/* List section (right) */}
        <aside className="lb-list">
          <div className="lb-card">
            <h2 className="lb-title">Leaderboard</h2>

            <div className="lb-controls">
              <input
                type="text"
                placeholder="Search schoolâ€¦"
                value={query}
                onChange={(e) => setQuery(e.target.value)}
              />
            </div>

            <div className="lb-list-body">
              <div className="lb-placeholder">List goes here</div>
            </div>
          </div>
        </aside>
      </main>
      <Footer />
    </>
  );
}
