import React, { useEffect, useState } from "react";
import "./QuizGame.css";
import Texts from "./Text";

const QuizGame = () => {
    const [questions, setQuestions] = useState([]);
    const [currentIndex, setCurrentIndex] = useState(0);
    const [feedback, setFeedback] = useState("");
    const [citizenCount, setCitizenCount] = useState(1);

    const CiviliansSprites = [
        require("../../assets/civ1.png"),
        require("../../assets/civ2.png"),
        require("../../assets/civ3.png"),
    ];

    useEffect(() => {
        const fetchQuestions = async () => {
            try {
                const res = await fetch("/api/questions/1");
                const data = await res.json();
                setQuestions(data);
            } catch (err) {
                console.error("Failed to fetch questions:", err);
            }
        };

        fetchQuestions();
    }, []);

    if (questions.length === 0) return <div>טוען שאלות...</div>;

    const currentQuestion = questions[currentIndex];

    const handleAnswerClick = (index) => {
        if (index === currentQuestion.correctAnswerIndex) {
            setFeedback(Texts.correct_answer_alert);
            setCitizenCount((prev) => Math.min(prev + 1, CiviliansSprites.length));
        } else {
            setFeedback(Texts.wrong_answer_alert);
            setCitizenCount((prev) => Math.max(prev - 1, 1));
        }

        setTimeout(() => {
            setFeedback("");
            setCurrentIndex((prev) =>
                prev + 1 < questions.length ? prev + 1 : prev
            );
        }, 1200);
    };

    return (
        <div className="quiz-container">
            {/* צד שמאל */}
            <div className="left-side">
                <div className="info-panel">
                    <div className="citizen-count-box">
                        {Texts.citizen_counter} {citizenCount}
                    </div>
                    <div className="feedback-box">{feedback}</div>
                    <div className="game-frame">
                        <div className="citizens-panel">
                            {CiviliansSprites.slice(0, citizenCount).map((src, index) => (
                                <img
                                    key={index}
                                    src={src}
                                    alt={`אזרח ${index + 1}`}
                                    className="citizen-img"
                                />
                            ))}
                        </div>
                    </div>
                </div>
            </div>

            {/* צד ימין */}
            <div className="right-side">
                <div className="question-box">{currentQuestion.question}</div>
                <div className="answers-grid">
                    {currentQuestion.answers.map((ans, idx) => (
                        <button
                            key={idx}
                            className="answer-button"
                            onClick={() => handleAnswerClick(idx)}
                        >
                            {ans}
                        </button>
                    ))}
                </div>
            </div>
        </div>
    );
};

export default QuizGame;
