// client/src/components/QuizGame/QuizGame.jsx
import React, { useEffect, useMemo, useState } from "react";
import "./QuizGame.css";
import Texts from "./Text";

// Sprites from src/assets (adjust path if needed)
import civ1 from "../../assets/civ1.png";
import civ2 from "../../assets/civ2.png";
import civ3 from "../../assets/civ3.png";

const CIVILIANS = [civ1, civ2, civ3];

const QuizGame = ({ quizId, onFinish }) => {
    const [questions, setQuestions] = useState([]);
    const [currentIndex, setCurrentIndex] = useState(0);
    const [feedback, setFeedback] = useState("");
    const [citizenCount, setCitizenCount] = useState(1);
    const [score, setScore] = useState(0);
    const [isLocked, setIsLocked] = useState(false);
    const [isLoading, setIsLoading] = useState(false);
    const [loadError, setLoadError] = useState("");
    const [finished, setFinished] = useState(false);

    // טוען מהשרת ומנרמל בדיוק לפי העמודות שב-SQL
    useEffect(() => {
        const fetchQuestions = async () => {
            if (!quizId) return;
            setIsLoading(true);
            setLoadError("");
            try {
                const res = await fetch(`/api/questions/${quizId}`);
                if (!res.ok) throw new Error(`HTTP ${res.status}`);
                const raw = await res.json();

                if (!Array.isArray(raw) || raw.length === 0) {
                    throw new Error("Empty questions array");
                }

                // ←← מיפוי ישיר: answer_1..answer_4 + correct_answer_index (1-based)
                const normalized = raw.map((r) => {
                    const answers = [
                        r.answer_1,
                        r.answer_2,
                        r.answer_3,
                        r.answer_4,
                    ].map((x) => String(x));

                    // המרה ל-0-based בצורה בטוחה
                    const idx1 = Number(r.correct_answer_index);
                    const idx0 =
                        Number.isFinite(idx1) && idx1 >= 1 && idx1 <= answers.length
                            ? idx1 - 1
                            : 0;

                    return {
                        question: r.question,
                        answers,
                        correctAnswerIndex: idx0,
                    };
                });

                setQuestions(normalized);
            } catch (err) {
                console.error("Failed to fetch questions:", err);
                setLoadError("Failed to load questions. Please try again.");
            } finally {
                setIsLoading(false);
            }
        };

        fetchQuestions();
    }, [quizId]);

    // איפוס מצב כשמשנים קוויז
    useEffect(() => {
        setQuestions([]);
        setCurrentIndex(0);
        setFeedback("");
        setCitizenCount(1);
        setScore(0);
        setIsLocked(false);
        setIsLoading(false);
        setLoadError("");
        setFinished(false);
    }, [quizId]);

    const currentQuestion = useMemo(
        () => (questions.length ? questions[currentIndex] : null),
        [questions, currentIndex]
    );

    if (!quizId) return <div>{Texts.no_quiz_selected}</div>;
    if (isLoading) return <div>{Texts.loading_questions}</div>;
    if (loadError) return <div>{loadError}</div>;
    if (!questions.length || !currentQuestion) return <div>{Texts.loading_questions}</div>;

    const handleAnswerClick = (index) => {
        if (isLocked || finished) return;
        setIsLocked(true);

        const isCorrect = index === currentQuestion.correctAnswerIndex;

        if (isCorrect) {
            setFeedback(Texts.correct_answer_alert);
            setScore((s) => s + 1);
            setCitizenCount((prev) => Math.min(prev + 1, CIVILIANS.length));
        } else {
            setFeedback(Texts.wrong_answer_alert);
            setCitizenCount((prev) => Math.max(prev - 1, 1));
        }

        setTimeout(() => {
            setFeedback("");
            setIsLocked(false);

            setCurrentIndex((prev) => {
                const next = prev + 1;
                if (next < questions.length) return next;

                // סיום
                const finalScore = isCorrect ? score + 1 : score;
                setFinished(true);
                if (typeof onFinish === "function") {
                    onFinish({ score: finalScore, total: questions.length });
                }
                return prev;
            });
        }, 800);
    };

    const handleRestart = () => {
        setCurrentIndex(0);
        setFeedback("");
        setCitizenCount(1);
        setScore(0);
        setIsLocked(false);
        setFinished(false);
    };

    return (
        <div className="quiz-container">
            {/* LEFT */}
            <div className="left-side">
                <div className="info-panel">
                    <div className="citizen-count-box">
                        {Texts.citizen_counter_label} {citizenCount}
                    </div>

                    <div className={`feedback-box ${feedback ? "visible" : ""}`}>
                        {feedback}
                    </div>

                    <div className="game-frame">
                        <div className="citizens-panel">
                            {CIVILIANS.slice(0, citizenCount).map((src, idx) => (
                                <img
                                    key={idx}
                                    src={src}
                                    alt={`Citizen ${idx + 1}`}
                                    className="citizen-img"
                                    onError={() =>
                                        console.warn("Sprite failed to load. Check path:", src)
                                    }
                                />
                            ))}
                        </div>
                    </div>
                </div>
            </div>

            {/* RIGHT */}
            <div className="right-side">
                {finished ? (
                    <div className="end-screen">
                        <h3>{Texts.end_title}</h3>
                        <p>{Texts.end_summary(score, questions.length)}</p>
                        <button className="restart-button" onClick={handleRestart}>
                            {Texts.restart}
                        </button>
                    </div>
                ) : (
                    <>
                        <div className="question-box">{currentQuestion.question}</div>
                        <div className="answers-grid">
                            {currentQuestion.answers.map((ans, idx) => (
                                <button
                                    key={idx}
                                    className="answer-button"
                                    onClick={() => handleAnswerClick(idx)}
                                    disabled={isLocked}
                                >
                                    {ans}
                                </button>
                            ))}
                        </div>
                    </>
                )}
            </div>
        </div>
    );
};

export default QuizGame;
