import React from "react";
import { GoogleMap, useJsApiLoader } from "@react-google-maps/api";

const containerStyle = { width: "100%", height: "100%" };
// Center on Israel roughly
const center = { lat: 31.5, lng: 34.75 };

export default function SchoolMap() {
    const { isLoaded, loadError } = useJsApiLoader({
        id: "qq-map",
        googleMapsApiKey: process.env.REACT_APP_GOOGLE_MAPS_API_KEY,
    });

    if (loadError) return <div className="lb-placeholder">Failed to load map</div>;
    if (!isLoaded) return <div className="lb-placeholder">Loading mapâ€¦</div>;

    return (
        <GoogleMap
            mapContainerStyle={containerStyle}
            center={center}
            zoom={7}
            options={{ disableDefaultUI: true, zoomControl: true }}
        >
            {/* markers will come later */}
        </GoogleMap>
    );
}
