import React, { useEffect, useState } from "react";
import "./QuizGame.css";
import Texts from "./Text";

const QuizGame = () => {
    const [questions, setQuestions] = useState([]);
    const [currentIndex, setCurrentIndex] = useState(0);
    const CiviliansSprites = [
        require("../../assets/civ1.png"),
        require("../../assets/civ2.png"),
        require("../../assets/civ3.png"),
    ];
    let correct = true;
    let citizen_count = 0;
    useEffect(() => {
        const fetchQuestions = async () => {
            try {
                const res = await fetch("/api/questions/1"); // אפשר להחליף ל-id משתנה בעתיד
                const data = await res.json();
                setQuestions(data);
            } catch (err) {
                console.error("Failed to fetch questions:", err);
            }
        };

        fetchQuestions();
    }, []);

    if (questions.length === 0) return <div>טוען שאלות...</div>;

    const currentQuestion = questions[currentIndex];

    return (
        <div className="quiz-container">
            <div className="left-side">
                <div className="info-panel">
                    <div className="citizen-count-box">{Texts.citizen_counter}{citizen_count}</div>
                    <div className="feedback-box">{correct ? Texts.correct_answer_alert : Texts.wront_answer_alert}</div>
                    <div className="game-frame">
                        {/* צד שמאל */}
                        <div className="citizens-panel">
                            {CiviliansSprites.map((src, index) => (
                                <img
                                    key={index}
                                    src={src}
                                    alt={`אזרח ${index + 1}`}
                                    className="citizen-img"
                                />
                            ))}
                        </div>
                    </div>

                </div>
            </div>

            {/* צד ימין */}
            <div className="right-side">
                <div className="question-box">כמה זה 2 + 2?</div>
                <div className="answers-grid">
                    <button className="answer-button">1</button>
                    <button className="answer-button">4</button>
                    <button className="answer-button">3</button>
                    <button className="answer-button">2</button>
                </div>
            </div>
        </div>
    );
};

export default QuizGame;
